//how to convert user sensitive data to hashed format by using Bcryptjs
const bcrypt=require('bcryptjs')
let user={id:101,name:"rahul",email:"rahul@gmail.com",password:"12345"}
let salt = bcrypt.genSaltSync(10)
let new_password=bcrypt.hashSync(user.password,salt)
console.log(new_password)
//now we use spread operator
let new_user={...user,password:new_password}
console.log(new_user)
//to check the normal and bcrypt passwords we can use comparesync
let flag=bcrypt.compareSync("12345",new_user.password)
//console.log(flag)
if(flag){
    console.log("login sucessful")
}
else{
    console.log("login failure")
}